package com.srm.platform.vendor.controller;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;

import com.srm.platform.vendor.model.Account;
import com.srm.platform.vendor.repository.AccountRepository;
import com.srm.platform.vendor.service.AccountService;

@Controller // This means that this class is a Controller
@RequestMapping(path = "/admin") // This means URL's start with /demo (after Application path)
public class AccountController {
	private final Logger logger = LoggerFactory.getLogger(this.getClass());

	@Autowired // This means to get the bean called userRepository
				// Which is auto-generated by Spring, we will use it to handle the data
	private AccountRepository accountRepository;

	@Autowired
	private AccountService accountService;

	@GetMapping(path = "/users/add") // Map ONLY GET Requests
	public @ResponseBody String addNewUser(@RequestParam String username, @RequestParam String password) {
		// @ResponseBody means the returned String is the response, not a view name
		// @RequestParam means it is a parameter from the GET or POST request

		logger.info("start user add");
		Account n = new Account(username, password, "ROLE_USER");
		n.setTo_account("jxylxx");
		accountService.save(n);

		return "Saved";
	}

	@GetMapping(path = "/users")
	public @ResponseBody Iterable<Account> getAllUsers() {

		// This returns a JSON or XML with the users
		return accountRepository.findAll();
	}
}
